["lore"] = {
	["A Treatise on Military Ranks"] = {
		["translation"] = "Eine Abhandlung über die militärischen Ränge",
		["pages"] = {
			"<HTML><BODY><H1 align=\"center\">ABHANDLUNG MILITÄRISCHER RÄNGE</H1><BR/><BR/><IMG src=\"Interface/PvPRankBadges/PvPRankAlliance\" align=\"left\" /><BR/><P align=\"right\">Es folgen</P><P align=\"right\">die militärischen Ränge</P><P align=\"right\">der Allianz,</P><P align=\"right\">gefolgt von</P><P align=\"right\">Informationen die</P><P align=\"right\">den Völkern</P><P align=\"right\">der Horde bekannt sind. Alle</P><P align=\"right\">Angaben sind in zwei</P><P align=\"right\">Abschnitte unterteilt, Offiziere und Soldaten. Dabei werden ihre Ränge jeweils in absteigender Reihenfolge aufgelistet, vom höchsten bis zum niedrigsten Rang. Lang lebe die Allianz!</P></BODY></HTML>",
			"<HTML><BODY><H1 align=\"center\">OFFIZIERSRÄNGE DER ALLIANZ</H1><BR/><P align=\"center\">Teil 1</P><IMG src=\"Interface/PvPRankBadges/PvPRank14\" align=\"left\" /><P align=\"right\">Großmarschall</P><BR/><BR/><IMG src=\"Interface/PvPRankBadges/PvPRank13\" align=\"left\" /><P align=\"right\">Feldmarschall</P><BR/><BR/><IMG src=\"Interface/PvPRankBadges/PvPRank12\" align=\"left\" /><P align=\"right\">Marschall</P><BR/><BR/><IMG src=\"Interface/PvPRankBadges/PvPRank11\" align=\"left\" /><P align=\"right\">Rittmeister</P><BR/><BR/><IMG src=\"Interface/PvPRankBadges/PvPRank10\" align=\"left\" /><P align=\"right\">Feldkommandant</P></BODY></HTML>",
