	["Ferren Marcus"] = {
		["translation"] = "Ferren Marcus",
		["pages"] = {
			"Ferren Marcus\nHochabt des Scharlachroten Klosters \nBürger von Stratholme \nFiel bei der Verteidigung des Scharlachroten Klosters im ersten Sommerzeitangriff",
		},
